String get_favicon(StopLightState state) {
    switch(state) {
        case STATE_FAILED:
            return favicon_f;
        case STATE_PREPARE:
            return favicon_p;
        case STATE_SUCCESS:
            return favicon_s;
        case STATE_UNSTABLE:
            return favicon_u;
        case STATE_INIT:
            return favicon_i;
        case STATE_OFF:
            return favicon_o;
        case STATE_NIGHT:
            return favicon_n;
        case STATE_BUILDING:
            return favicon_b;
        case STATE_WAIT:
            return favicon_w;
        case STATE_ALTERNATE:
            return favicon_a;
        case STATE_ROLLING:
            return favicon_r;
        case STATE_PARTY:
            return favicon_$;
        case STATE_CYCLE:
            return favicon_c;
        default:
            return favicon_wtf;
    }
}

